import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Button, Paper, Typography, Modal } from "@material-ui/core";

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    margin: "auto",
    transform: `translate(-${top}%, -${left}%)`,
  };
}
const useStyles = makeStyles((theme) => ({}));

/***
 * Author
 * Nargel Velasco - Tech Team
 * @param {Object} data - header and rows data
 * @param {Function} exportHandler - handle export function
 * @param {Function} closeModal - close modal
 * @param {Function} isOpen - open modal if true
 *
 */

export default function YesNoModal(props) {
  const { noHandler, yesHandler, description, isOpen } = props;
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [modalStyle] = useState(getModalStyle);
  const handleClose = () => {
    setOpen(false);
  };
  const body = (
    <Paper elevation={2} style={{ width: "25%", height: "150px" }}>
      <div style={{ paddingRight: 20, paddingLeft: 20, paddingTop: 20 }}>
        <Typography variant="body1">{description}?</Typography>
        <br />
        <div style={{ display: "inline-flex", gap: 10 }}>
          <Button
            variant="contained"
            color="secondary"
            onClick={() => noHandler()}
          >
            {" "}
            No
          </Button>
          <Button
            variant="contained"
            color="primary"
            onClick={() => yesHandler()}
          >
            Yes
          </Button>
        </div>
      </div>
    </Paper>
  );

  return (
    <div>
      <Modal
        fullWidth={true}
        maxWidth={"300px"}
        maxHeight={"300px"}
        open={isOpen ? true : false}
        onClose={handleClose}
        aria-labelledby="yn-modal"
        aria-describedby="yes-or-no"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {body}
      </Modal>
    </div>
  );
}
